<template>
  <section class="w-screen h-screen flex flex-col items-center justify-center text-center bg-white">
    <!-- Full-Width Image -->
    <img src="/static/karte-info.jpg" alt="Karte Info" class="w-full h-screen object-cover" />

    <!-- Text Below -->
    <div class="w-full max-w-4xl px-6 py-12">
      <h2 class="text-4xl font-bold text-gray-900">「カルテ」って何？</h2>
      <p class="text-lg text-gray-700 mt-4">
        EYS-Kids独自のレッスンシステム。<br />
        自分のレッスンを毎回可視化でき、<br />
        自身のスキルアップが確実に実感できます！
      </p>
    </div>
  </section>
</template>
<template>
  <div class="bg-white">
    <HeroSection />
    <KarteInfo />
    <FeaturesGrid />
    <FooterBanner />
    <div class="view-more">
      <button @click="viewMore">VIEW MORE</button>
    </div>
  </div>
</template>

<script>
import HeroSection from '/components/HeroSection.vue';
import KarteInfo from '/components/KarteInfo.vue';
import FeaturesGrid from '/components/FeaturesGrid.vue';
import FooterBanner from '/components/FooterBanner.vue';

export default {
  methods: {
    viewMore() {
      // Logic to navigate to more information
      this.$router.push("/instructors");
    },
  },
  components: {
    HeroSection,
    KarteInfo,
    FeaturesGrid,
    FooterBanner,
  },
};
</script>
<style scoped>
.view-more {
  margin-top: 20px;
  text-align: center;
}

.view-more button {
  padding: 10px 20px;
  background-color: #007bff;
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;
}

.view-more button:hover {
  background-color: #0056b3;
}
</style>
